#define sb 505
int dp[sb][sb];
int q[505];
int n;
int min(int a,int b)
{
	return a<b?a:b;
}
int dfs(int l,int r)
{
	if(dp[l][r]!=-1)
	return dp[l][r];
	if(l==r)
	return 1;
	if(l>r)
	return 0;
	int ans=1+dfs(l+1,r);
    for (int i=l+2;i<=r;i++) 
	if (q[l]==q[i])
    ans=min(ans,dfs(l+1,i-1)+dfs(i+1,r));
    if(q[l]==q[l+1]) 
	ans=min(ans,1+dfs(l+2,r));
    return ans;
}
int main()
{
scanf("%d",&n);
for(int i=1;i<=n;i++)
scanf("%d",&q[i]);
memset(dp,-1,sizeof(dp));
printf("%d",dfs(1,n));
return 0;
}

