int dat;

void gsec(struct pos *pp)
{
	pp->sec=(long long)(pp->init)-(long long)(pp->date)*(long long)(pp->gain);
}

int gdat(struct pos *pp,long long tgt)
{
	long long dd;
	tgt-=pp->sec;
	dd=(tgt-1)/(long long)(pp->gain)+1;
	return (int)dd;
}

long long gval(struct pos *pp,int dd)
{
	return pp->sec+(long long)dd*(long long)(pp->gain);
}

