#include <stdio.h>
#include <stdlib.h>
#include <stdint.h>
#include <inttypes.h>



uint64_t min(uint64_t a, uint64_t g) {
	
	uint64_t min1, min2;
	
	min1 = a%g;
	min2 = g - (a%g);
	
	if (min1 < min2) {
		
		return min1;
	}
	else {
		
		return min2;
	}
}

int main() {
	
	int numTests = 0, test = 1;
		
	uint64_t a, b, g, minRes;
	
	scanf("%d", &numTests);
		
	while (test <= numTests) {
		
		scanf("%" SCNu64 "%" SCNu64, &a, &b);
		
		if (a == b) {
		
			printf("0 0\n");
		}
		else {
		
		g = abs(a - b);
		
		minRes = min(a, g);
		
		printf("%" PRIu64 " ", g);
		printf("%" PRIu64 "\n", minRes);
		}
		test++;
	}
	
	return 0;
}

