#include <stdio.h>

#define N	400

int greedy(int *aa, int n, int w) {
	int cnt, i;

	cnt = 0;
	for (i = 0; i < n; i++) {
		cnt += w / aa[i];
		w %= aa[i];
	}
	return cnt;
}

int main() {
	static int aa[N];
	int n, i, j, cnt, x, y;

	scanf("%d", &n);
	for (i = 0; i < n; i++)
		scanf("%d", &aa[i]);
	for (i = 1; i < n; i++) {
		cnt = 0;
		y = 0;
		x = aa[i - 1] - 1;
		for (j = i; j < n; j++) {
			cnt += x / aa[j];
			y += x / aa[j] * aa[j];
			x %= aa[j];
			if (greedy(aa, n, y + aa[j]) > cnt + 1) {
				printf("%d\n", y + aa[j]);
				return 0;
			}
		}
	}
	printf("-1\n");
	return 0;
}

