#include<stdio.h>
#include<stdlib.h>
int n,q;
struct a{
	int l,r,x;
}a[10007];
int x[10007];
int qdiff(const void *p,const void *q){
	return ((struct a *)p)->l-((struct a *)q)->l;
}
void cat(){
	int i,j,k;
	qsort(a,q,sizeof(a[0]),qdiff);
	for(i=0;i<=n;i++){
		x[i]=-1;
	}
	x[0]=n;
	for(i=0;i<q;i++){
		for(j=n;j>=0;j--){
			k=j+a[i].x;
			if(x[j]<a[i].l||k>n){
				continue;
			}
			x[k]=x[k]>a[i].r?x[k]:a[i].r;
		}
	}
	j=0;
	for(i=1;i<=n;i++){
		if(x[i]!=-1){
			j++;
		}
	}
	printf("%d\n",j);
	for(i=1;i<=n;i++){
		if(x[i]!=-1){
			printf("%d ",i);
		}
	}
	printf("\n");
}
void run(){
	int i;
	while(scanf("%d%d",&n,&q)!=EOF){
		for(i=0;i<q;i++){
			scanf("%d%d%d",&a[i].l,&a[i].r,&a[i].x);
			a[i].l--;
			a[i].r--;
		}
		cat();
	}
}
