#include <stdio.h>
#include <stdlib.h>
#define NMAX 100100
#define MIN(a, b) (((a) < (b)) ? (a) : (b))
#define MAX(a, b) (((a) > (b)) ? (a) : (b))

typedef struct {
	int u, v;
} seg_t;

seg_t seg[NMAX];

void swap(int *a, int *b) {
	int tmp = *a;
	*a = *b;
	*b = tmp;
}

int comp(const void *s1, const void *s2) {
	const seg_t *s11 = (const seg_t *)s1,
				*s21 = (const seg_t *)s2;
	if (s11->u != s21->u)
		return s11->u - s21->u;
	return s11->v - s11->u - s21->v + s21->u;
}

int main(void) {
	int n, m, i;
	seg_t it;
	scanf("%d%d", &n, &m);

	for (i = 0; i < m; ++i) {
		scanf("%d%d", &seg[i].u, &seg[i].v);
		if (seg[i].u > seg[i].v)
			swap(&seg[i].u, &seg[i].v);
	}

	qsort((void *)seg, m, sizeof(seg_t), comp);

	it.u = MAX(seg[0].u, seg[m - 1].u);
	it.v = MIN(seg[0].v, seg[m - 1].v);

	printf("%d", MAX(0, it.v - it.u));

	return 0;
}

