bool dfs(int i, const int incid[2][N][N+1], bool whatta[N]) {
	int j, k;
	if (whatta[i])
		return false;
	whatta[i] = true;
	for (j = 1; j <= incid[0][i][0]; j ++) {
		int jj = incid[0][i][j];
		for (k = 1; k <= incid[1][jj][0]; k ++) {
			int kk = incid[1][jj][k];
			(void) dfs(kk, incid, whatta);
		};
	};
	return true;
};

