#include <stdio.h>

void swap(int *a, int *b);
void quicksort(int arr[], int l, int r);

int main(void) {

    int n, a;
    scanf("%d", &n);
    int number[n];

    for (int i = 0; i < n; ++i) {
        scanf("%d", &number[i]);
    }

    quicksort(number, 0, n-1);

    int temp = n - 2;

    for (int i = 0; i < temp; ++i) {
        if (number[i] + number[i + 1] == number[i + 2]) {
            printf("%d %d %d\n", number[i + 2], number[i+1], number[i]);
            return 0;
        }
    }

    printf("-1\n");
    return -1;

}

void quicksort(int arr[], int l, int r) {
    if (l >= r)
    {
        return;
    }

    int pivot = arr[r];
    int cnt = l;
    for (int i = l; i <= r; i++)
    {
        if (arr[i] <= pivot)
        {
            swap(&arr[cnt], &arr[i]);
            cnt++;
        }
    }
    quicksort(arr, l, cnt-2); 
    quicksort(arr, cnt, r);   
}

void swap(int *a, int *b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

