#include <stdio.h>
void swap(int *a, int *b);
int main()
{
	int a[50], b[50], sum[50];
	int r;
	int n, x;
	scanf("%d", &r);
	for (int i = 0; i < r; i++) {
		scanf("%d %d", &n, &x);
		for (int j = 0; j < n; j++)
			scanf("%d", &a[j]);
		for (int j = 0; j < n; j++)
			scanf("%d", &b[j]);

		for (int j = 0; j < n; j++) {
			for (int k = 0; k < n - j - 1; k++) {
				if (a[k] > a[k + 1])
					swap(&a[k], &a[k + 1]);
			}
		}

		for (int j = 0; j < n; j++) {
			for (int k = 0; k < n - j - 1; k++) {
				if (b[k] < b[k + 1]) 
					swap(&b[k], &b[k + 1]);
			}
		}

		int max = a[0] + b[0];
		for (int j = 0; j < n; j++) {
			if (a[j] + b[j] > max)
				max = a[j] + b[j];
		}
		if (max > x)
			printf("NO\n");
		else
			printf("Yes");
	}
	return 0;
}

void swap(int *a, int *b)
{
	int temp = *a;
	*a = *b;
	*b = temp;
}

