#include<stdio.h>
#include<stdlib.h>

int min(int a, int b) {
	return (a < b ? a : b);
}
int max(int a, int b) {
	return (a > b ? a : b);
}
int lower_bound(int a[], int l, int r, int x) {
	int ans = r + 1;
	int m;
	while(l <= r) {
		m = (l + r) / 2;
		if (a[m] >= x) {
			ans = min(ans, m);
			r = m - 1;
		} else {
			l = m + 1;
		}
	}
	return ans;
}

int main() {
	int n;
	scanf("%d", &n);
	int a[n];
	for (int i = 0; i < n; i++) {
		scanf("%d", a + i);
	}
	int ans = 0, up;
	for (int i = 0; i < n; i++) {
		up = lower_bound(a, i + 1, n - 1, 2 * a[i] + 1);
		ans = max(ans, up - i);
		if (up == n) {
			break;
		}
	}
	printf("%d\n", ans);
	return 0;
}

