#include <math.h>

int solve(int a, int b, int t1, int f1, int t2, int f2) {
    int ans = abs(t1 - t2);
    ans += abs(f1 - f2);
    if (f1 > b && f2 > b) {
        int min = (f1-b < f2-b ? f1-b : f2-b);
        ans += (2*min);
    } else if (f1 < a && f2 < a) {
        int min = (a-f1 < a-f2 ? a-f1 : a-f2);
        ans += (2*min);
    }
    return ans;
}

