#include <stdio.h>

#define N 5000

int a[N], b[N];

void sort(int l, int r) {
    int i = l, j = r, pp = (l + r) / 2, p1 = a[pp], p2 = b[pp], tmp;
    while (i <= j) {
        while (a[i] < p1 || a[i] == p1 && b[i] < p2) ++i;
        while (p1 < a[j] || p1 == a[j] && p2 < b[j]) --j;
        if (i <= j) {
            tmp = a[i]; a[i] = a[j]; a[j] = tmp;
            tmp = b[i]; b[i] = b[j]; b[j] = tmp;
            ++i; --j;
        }
    }
    if (i < r) sort(i, r);
    if (l < j) sort(l, j);
}

int main(void) {
    int n, i, lastReal = -1;

    scanf("%d", &n);
    for (i = 0; i < n; ++i) scanf("%d %d", &a[i], &b[i]);

    sort(0, n - 1);

    lastReal = b[0];
    for (i = 1; i < n; ++i) {
        lastReal = b[i] >= b[i - 1] ? b[i] : a[i];
    }

    printf("%d\n", lastReal);
    return 0;
}

