{
	double s1,s2;
	if(x1==x2)
		s1=max;
	else
		s1=(y2-y1)/(x2-x1);
	if(x3==x2)
		s2=max;
	else
		s2=(y3-y2)/(x3-x2);
	if(s1==s2)
		return s1;
	else
		return min;

}
