        for(i = 1; i < n; i++)
        {
            scanf(" %ld", &B[i]);
            if(minb > B[i])
                minb = B[i];
        }

        //go through the arrays and count
        count = 0;
        for(i = 0; i < n; i++)
        {
            if (A[i] > mina)
            {
                count += A[i] - mina;
                B[i] -= A[i] - mina;
            }

            if (B[i] > minb)
                count += B[i] - minb;
        }

        printf("%ld \n", count);
    }
