#include<stdio.h>
#define ll long long
ll arr[200000]={0};
void merge(ll arr[],ll L[],ll R[],int l,int r)
{
        int i=0,j=0,k=0;
        while(i<l && j<r)
        {
                if(L[i]<=R[j])
                        arr[k++]=L[i++];
                else 
                        arr[k++]=R[j++];
        }
        while(i<l)
                arr[k++]=L[i++];
        while(j<r)
                arr[k++]=R[j++];
        return ;
}
void sort(ll arr[],int n)
{
	int mid=n/2,l=mid,r=n-mid,p;
	if(n<2)
		return;
	ll L[mid],R[n-mid];
	for(p=0;p<mid;p++)
		L[p]=arr[p];
	for(p=0;p<n-mid;p++)
		R[p]=arr[p+mid];
	sort(L,l);
	sort(R,r);
	merge(arr,L,R,l,r);
}
int main()
{
	int n,a,b;
	scanf("%d%d",&n,&a);
	b=n;
	for(b=0;b<n;b++)
		scanf("%lld",&arr[b]);
	sort(arr,n);
	if(a==0)
	{	
		arr[0]==1?printf("-1\n"):printf("%lld\n",arr[0]-1);
	}
	else if(a==n && arr[n-1]==1000000000)
		printf("1000000000\n");
	else
	{
		arr[a-1]==arr[a]?printf("-1\n"):printf("%lld\n",arr[a-1]+1);
	}
}

