void max_hayble_in_pile1(int*a,int n,int d)
{
	int ans = a[0];
	for(int i=1;i<n;i++){
		if(a[i]==0)
		continue;
		if(a[i]*(i)>d)
		{
			ans+=(d/i);
			break;
		}
		else if(a[i]*i==d)
		{
			ans+=a[i];
			break;
		}
		else
		{
			ans+=a[i];
			d-=a[i];
		}
	}
	printf("%d\n",ans);
}
