#include<stdio.h>
#define	LLD	"%lld"
int n;
long long p,q,b;
long long gcd(long long p,long long q){
	if(q==0){
		return p;
	}
	return gcd(q,p%q);
}
void run(){
	int i;
	long long x;
	while(scanf("%d",&n)!=EOF){
		for(i=0;i<n;i++){
			scanf(LLD LLD LLD,&p,&q,&b);
			if(p==0){
				printf("Finite\n");
				continue;
			}
			x=gcd(p,q);
			if(q==x){
				printf("Finite\n");
				continue;
			}
			q/=x;
			x=b;
			while(x!=1){
				x=gcd(q,x);
				do{
					q/=x;
				}while(q%x==0);
			}
			if(q==1){
				printf("Finite\n");
				continue;
			}
			printf("Infinite\n");
		}
	}
}
main(){
	run();
	return 0;
}

