#include <stdint.h>
#include <inttypes.h>
#include <memory.h>
#define N 200005
int a[N];
int64_t yc[2][N];
size_t n;

int64_t walk(size_t i, int odd) {
	if (!i)
		return -1;

	if (yc[odd][i]) {
		return yc[odd][i];
	} else {
		int j = odd ? (i - a[i]) : (i + a[i]);
		if (j < 0 || j >= n)
			return (yc[odd][i] = a[i]);
		else {
			int64_t y;
			yc[odd][i] = -1;
		   	y = walk(j, !odd);
			if (y < 0)
				return -1;
			return (yc[odd][i] = a[i] + y);
		};
	};
};

int main(void) {
	size_t i;
	scanf("%zd", &n);
	for (i = 1; i < n; i++) {
		scanf("%d", &a[i]);
	};
	memset(yc[0], 0, sizeof(yc[0]));
	memset(yc[1], 0, sizeof(yc[1]));
	for (i = 1; i < n; i++) {
		int64_t y = walk(i, 1);
		printf("%" PRId64 "\n", (y < 0)?(int64_t)-1:(i+y));
	};
	return 0;
};

