            y[j*i]=1;
            j=j*i;
        }
    }
    int counter=0;
    for(i=0; i<=N; i++) {
        if(y[i]==1) counter++;
    }
    printf("%d\n",counter);
    for(i=0; i<=N; i++) {
        if(y[i]==1) printf(" %d",i);
    }
    printf("\n");
    return 0;
}

