#include <stdio.h>

#define N 200000

int solve(int *a, int n) {
    int l, k = 1, s = 0;

    while (1) {
        for (; k < n; k++) {
            if (a[n - 1 - k] != a[n - 1]) break;
        }

        if (k >= n) {
            return s;
        }

        for (l = n - k * 2; l < n - k - 1; l++) {
            a[l] = a[n-1];
        }

        k *= 2;
        s++;
    }

    return 0;
}

int main() {
    int T, a[N], n;

    scanf("%d", &T);

    for (int t = 0; t < T; t++) {
        scanf("%d", &n);
        for (int i = 0; i < n; i++) {
            scanf("%d", &a[i]);
        }

        printf("%d\n", solve(a, n));
    }
}

