	if(r-l>=1){
		merge_sort(l,mid);
		merge_sort(mid+1,r);
	}
	p=l;q=mid+1;i=l;
	while(p<=mid||q<=r){
	if(q>r||(p<=mid&&a[p]<=a[q]))
		t[i++]=a[p++];
	else
