    int i, x, n, max = 0, min = 1000000001, n_max, n_min;
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &x);
        if (x > max)
        {
            max = x;
            n_max = 1;
        }
        else if (x == max)
            ++n_max;

        if (x < min)
        {
            min = x;
            n_min = 1;
        }
        else if (x == min)
            ++n_min;
    }
    if (max == min)
        printf("0 %d\n", (long long) n * (n - 1) / 2);
    else
        printf("%d %d\n", max - min, (long long) n_max * n_min);
    return 0;
}

