void merge_sort(int a[], int l, int r){
	if(l < r){
		merge_sort(a, l, (l + r) / 2);
		merge_sort(a, (l + r) / 2 + 1, r);
		merge(a, l, (l + r) / 2, r);
	}
}

int main(){
	int t, n, i, l, r;
	int a[100000];
	int s[100000];
	scanf("%d", &t);
	while(t--){
		scanf("%d", &n);
		for(i = 0; i < n; i++){
			scanf("%d", &a[i]);
		}
		merge_sort(a, 0, n - 1);
		for(i = 0; i < n; i++){
			if(i % 2 == 0){
				s[i] = a[n - 1 - i / 2];
			}
			else{
				s[i] = a[i / 2];
			}

		}
		for(i = n - 1; i >= 0; i--){
			printf("%d ", s[i]);
		}
		printf("\n");
	}

	return 0;
}

