#include <stdio.h>
#include <stdlib.h>
#define N 50

/*
 * 
 */
int max_increase(int A[N], int n){
    int i,j;
    int max=1;

    for(i=0; i<n-1; i++){
        int maxaux=1;
        for(j=i+1; j<n; j++){
            if(A[j]>A[j-1])
                maxaux++;
            else
                break;       
        }
        if (maxaux > max)
            max = maxaux;
    }

    return max;
}

int main(int argc, char** argv) {
    int n;
    scanf("%d", &n);

    int A[N];
    int i;
    for(i=0; i<n; i++)
        scanf("%d", &A[i]);

    int sol = max_increase(A, n);

    printf("%d\n", sol);
    return (EXIT_SUCCESS);
}

