int main()
{
    int n,max=0,p,min,count=0,i,j;
    scanf("%d",&n);
    int a[n];
    for(i=0;i<n;i++)
    {
        scanf("%d",&a[i]);
    }
    min=a[0];
    for(i=0;i<n;i++)
    {
        if(a[i]>max)
        {
            max=a[i];
        }
        if(a[i]<min)
        {
            min=a[i];
        }   
    }
    for(i=0;i<n;i++)
    {
        for(j=i;j<n;j++)
        {
            p=abs(a[i]-a[j]);
            if(p==(max-min))
            {
                ++count;
            }
        }
    }
    printf("%d %d",(max-min),count);
    return 0;
}

