#define min(a, b) (a < b ? a : b)
#define TRUE 1
#define FALSE 0
#define ll long long int

int freqNeg[200002];
int freqZero[200002];
int arr[200002];

int main(void) {
    freopen("input.txt","r", stdin);
    freopen("output.txt","w", stdout);
    int n, i, changes, res;
    scanf("%d", &n);
    for(i = 1; i <= n; i++) {
        scanf("%d", &arr[i]);
    }
    for(i = 1; i <= n; i++) {
        if(arr[i] == 0) {
            freqZero[i] += 1;
        }
        else if(arr[i] < 0) {
            freqNeg[i] += 1;
        }
        freqNeg[i] += freqNeg[i-1];
        freqZero[i] += freqZero[i-1];
    }
    res = INT_MAX - 2;
    for(i = 1; i < n; i++) {
        changes = i - freqNeg[i];
        changes += freqNeg[n] - freqNeg[i+1] + freqZero[n] - freqZero[i+1];
        res = min(res, changes);
    }
    printf("%d\n", res);
    return 0;
}

