void HeapAdjust(int *a, int s, int m)
{
	int rc, j;
	rc = a[s];
	for (j = 2 * s; j <= m; j = j * 2)
	{
		if (j < m&& a[j] < a[j + 1]) j++;
		if (rc > a[j]) break;
		a[s] = a[j]; s = j;
	}
	a[s] = rc;
}
void HeapSort(int *a, int n)
{
	int temp, i, j;
	for (i = n / 2; i > 0; i--)
	{
		HeapAdjust(a, i, n);
	}
	for (i = n; i > 0; i--)
	{
		temp = a[1];
		a[1] = a[i];
		a[i] = temp;
		HeapAdjust(a, 1, i - 1);
	}
	for (i = 0; i < n; i++)
		swap(&a[i], &a[i + 1]);
}
