#include <stdio.h>
#include <stdlib.h>
int main(void) {
	long long int N, num, ans;
	char c, *car;
	int flag = 0;
	scanf("%I64d\n", &N);
	car = (char*)malloc(sizeof(char)*N);
	for (long int i=0;i<N;i++) {
		scanf("%c", &car[i]);
	}
	scanf("%c", &c);
	ans = 0;
	for(long int i=0;i<N;i++) {
		scanf("%I64d", &num);
		if (car[i] == '<') {
			ans = ans - num;
			if (ans >= 0) {
				flag = 1;
				break;
			} else if (ans < 0) {
				flag = 0;
				break;
			}
		} else {
			ans = ans + num;
			if (ans > N) {
				flag = 0;
				break;
			}
		}
	}
	if (flag == 1) {
		printf("INFINITE");
	} else {
		printf("FINITE");
	}
	return 0;
}

