#include<stdio.h>
#include<stdlib.h>

int find_out(int x, int* fresh, int* heaps, int n) {
	int i,d;
	for(i=n-1;i>=2;i--) {
		if(fresh[i] < x) return 1;
		d = heaps[i] < (fresh[i]-x) ? heaps[i]/3 : (fresh[i]-x)/3;
		fresh[i] = fresh[i] - 3*d;
		fresh[i-1] = fresh[i-1] + d;
		fresh[i-2] = fresh[i-2] + 2*d;
	}
	return (fresh[0]>=x && fresh[1]>=x) ? 0 : 1;
}

void solve() {
	int n,i,sum,l,r;
	int* heaps, *fresh;
	scanf("%d", &n);
	heaps = malloc(n*sizeof(int));
	fresh = malloc(n*sizeof(int));
	sum=0;
	for(i=0;i<n;i++) {
		scanf("%d", &heaps[i]);
		sum = sum+heaps[i];
	}
	sum = sum/n;
	l=0;r=sum;
	while(l<r) {
		for(i=0;i<n;i++)
			fresh[i] = heaps[i];
		sum = l+(r-l+1)/2;
		if (find_out(sum, fresh, heaps, n) == 0)
			l = sum;
		else
			r = sum-1;
	}
	printf("%d\n", l);
}

int main() {
	int t;
	scanf("%d", &t);
	while(t--)
		solve();
	return 0;
}

