int cnt[3][100000];

int yes(int l, int r) {
	int x = cnt[0][r] - cnt[0][l - 1];
	int y = cnt[1][r] - cnt[1][l - 1];
	int z = cnt[2][r] - cnt[2][l - 1];
	int max = x, min = x;

	if (max < y)
		max = y;
	if (min > y)
		min = y;
	if (max < z)
		max = z;
	if (min > z)
		min = z;
	return (r - l + 1) % 3 == 0 ? min == max : min + 1 == max;
}

