void nhap_mang(int a[][2], int n){
	for(int i=0;i<n;i++){
		for(int j=0;j<2;j++)
			scanf("%d", &a[i][j]);
	}
}

int optimal(int a[][2], int n){
	int min=a[0][1];
	int sum=0;
	for(int i=1;i<n;i++){
		if(a[i][1]>=min)
			sum+=min*a[i-1][0];
		else{
			sum+=min*a[i-1][0];
			min=a[i][1];
		}
		if(i==n-1) sum+=min*a[n-1][0];
	}
	return sum;
}

int main(){
	int n;
	scanf("%d", &n);
	int a[n][2];
	nhap_mang(a,n);

	printf("%d", optimal(a,n));
	return 0;
}

