__int64 height(__int64 i){
    return a+(i-1)*b;
}
__int64 sum(__int64 r){
    return (height(r)+height(l))*(r-l+1)/2;
}

int main(int argc, char* argv[])
{

    scanf("%I64d%I64d%I64d",&a,&b,&n);
    while(n--){

        scanf("%I64d%I64d%I64d",&l,&t,&m);
        if(height(l)>t)
            printf("-1\n");
        else{
            __int64 low=l,h=(t-a)/b+1,mid;
            while(low<=h){
                mid=(low+h)/2;
                if(sum(mid)<=t*m)
                    low=mid+1;
                else
                    h=mid-1;
            }
            printf("%I64d\n",low-1);
        }
    }

    return 0;
}

