    int aux, h;
    long long i,j;
    for (i = 0; i < N; i++) {
        j = i;
        aux = lista[i];
          while (j > 0 && aux < lista[j - 1]) {
               lista[j] = lista[j - 1];
                 j--;
        }
        lista[j] = aux;
    }
    return lista[N-1];
}
int sumatoria(int a[],long long n){
   int i,suma;
   suma=0;
   for(i=0;i<n;i++){
    suma=suma+a[i];
   }
return suma;
}
