	while (n-- > 0)
	{
		fgets(line, 80, stdin) ;

		i = 0 ;		
		rc = 0 ;

		if (line[i] == 'R')
		{
			i++ ;

			while (line[i] && '0' <= line[i] && line[i] <= '9')
				i++ ;

			rc = (line[i] == 'C' && i > 1) ;
		}

