			column = 0 ;
			while (line[i] != '\n')
			{
				column *= 10 ;
				column += line[i] - '0' ;
				i++ ;
			}

			printColumn(column) ;
			fprintf(stdout, "%d\n", row) ;
		}
		else
		{
			column = 0 ;
			while ('A' <= line[i] && line[i] <= 'Z')
			{
				column *= 26 ;
				column += line[i] - 'A' + 1 ;
				i++ ;
