			row = 0 ;
			while (line[i] != '\n')
			{
				row *= 10 ;
				row += line[i] - '0' ;
				i++ ;
			}

			fprintf(stdout, "R%dC%d\n", row, column) ;
		}
	}

	return 0 ;
}

