#include <stdio.h>

int gcd(int x, int y)
{
	return (y == 0 ? x : gcd(y, x % y)) ;
}

void readpol(int* n, int* a)
{
  int i, tmp, trash ;

	for (i = 0, tmp = 0 ; i < *n + 1 ; i++)
	{
		scanf("%d", a) ;

		if (*a == 0)
		{
			++tmp ;
		}
		else
		{
		  break ;
		}
	}

	for ( ; i < *n ; i++)
	{
		scanf("%d", &trash) ;
	}

	*n -= tmp ;

	if (*n < 0)
	{
		*n = 0 ;
	}
}

int main()
{
  int a, b, m, n, s ;

	scanf("%d %d", &n, &m) ;

	readpol(&n, &a) ;
	readpol(&m, &b) ;

	if (n > m)
	{
		if (a > 0)
		{
			printf("Infinity\n") ;
		}
		else if (a < 0)
		{
			printf("-Infinity\n") ;
		}
	}
	else if (n < m)
	{
		printf("0/1\n") ;
	}
	else
	{
		s = a * b ;

		if (a < 0)
			a = -a ;

		if (b < 0)
		  b = -b ;

		if (s < 0)
		{
			s = gcd(a, b) ;
			printf("-%d/%d\n", a / s, b / s) ;
		}
		else
		{
			s = gcd(a, b) ;
			printf("%d/%d\n", a / s, b / s) ;
		}
	}

	return 0 ;
}

