	else
	{
		char *l1=strtok(s,"*");
		char *l2=strtok(NULL,"*");
		int r1=strlen(l1);
		int r2=strlen(l2);

		if(m>=n)
		{
			if(strncmp(l1,t,r1)==0)
			{
				if(strcmp(l2,t+m-r2)==0)
