#include<stdio.h>
#include<math.h>

#define MAXI 10000001

// Time
// O(n^2)

// Memory
// O(1)

int collected = 0;
int primes[MAXI];
void nPrimes(int n){
	if(n == 0){
		return;
	}

	primes[collected] = 2;
	collected += 1;
	n -= 1;

	for(int j = 3; j < MAXI && n > 0; j += 2){
		int k;
		// Dejar de buscar divisores al llegar a la raíz
		int f = 1;
		double root = sqrt(j);
		for(k = 0; k < collected && primes[k] < root; k++){
			if(j % primes[k] == 0){
				f = 0;
				break;
			}
		}

			if(f){
				primes[collected] = j;
				collected += 1;
				n -= 1;
			}
		}
}

int main(void){
	int n;
	scanf("%d", &n);

	nPrimes(n);

	for(int i = 0; i < collected - 1; i++){
		printf("%d ", primes[i]);
	}
	printf("%d\n", primes[collected - 1]);


	return 0;
}

