    int oriArr[n];

    // initialize the n studetns that are increased by 1 for each time
    for(int i=0; i<n; i++){
        arr[i] = i+1;
        oriArr[i] = i+1;
    } 
    int input;
    int temp = 0; 
    for(int i=0; i<m; i++){
        cin >> input;
