#include <stdio.h>
#include <stdlib.h>
#include <string.h>

void cswap(char **a, char **b) {
  char *temp = *a;
  *a = *b;
  *b = temp;
}

void swap(int *a, int *b) {
  int temp = *a;
  *a = *b;
  *b = temp;
}

void sort(int n, int m, char str[n + 1][m + 1], int *index, int len) {
  int pivot = 0;

  if (len <= 1) {
    return;
  }
  for (int i = 0; i < len; i++) {
    if (strcmp(str[index[i]], str[index[len - 1]]) < 0) {
      swap(index + i, index + pivot++);
    }
  }
  swap(index + pivot, index + len - 1);
  sort(n, m, str, index, pivot++);
  sort(n, m, str, index + pivot, len - pivot);
}

int main() {
  int n, m;
