    for (int j = 1; j < m; j += 2)
      temp[j] = 'Z' - temp[j] + 'A';
    strcpy(str[i], temp);
    index[i] = i;
  }
  sort(n, m, str, index, n);
  for (int i = 0; i < n; i++) {
    printf("%d ", index[i] + 1);
  }
}

