    int k = 0;

    scanf("%d %d", &n, &k);

    char s[n + 1];
    scanf("%s", s);

    int alpha[26] = {0};

    for (int i = 0; i < n; i++) {
        alpha[s[i] - 0x41]++;
    }

    qsort(alpha, 26, sizeof(int), cmp);

    int i = 25;
    int r = 0;

    while (i >= 0 && k > 0) {
        if (alpha[i] > k) {
            r += k * k;
            break;
        } else {
            r += alpha[i] * alpha[i];
            k -= alpha[i];
        }

        i--;
    }

    printf("%d\n", r);
    return 0;
}

