#define M 100000

int compare(const void *a, const void *b) {
	int ia = *(int *) a;
	int ib = *(int *) b;

	return ia - ib;
}

int main() {
	static long long pp[M], qq[M];
	long long n;
	int m, k, i, j, cnt, h, ans;

	scanf("%lld%d%d", &n, &m, &k);
	for (i = 0; i < m; i++)
		scanf("%lld", &pp[i]);
	ans = 0;
	for (i = 0; i < m; ) {
		j = i;
		cnt = 0;
		while (j < m && (pp[j] + k - 1) / k == (pp[i] + k - 1) / k) {
			qq[cnt++] = (pp[j] - j + k - 1) / k;
			j++;
		}
		qsort(qq, cnt, sizeof *qq, compare);
		ans++;
		for (h = 0; h + 1 < cnt; h++)
			if (qq[h] != qq[h + 1])
				ans++;
		i = j;
	}
	printf("%d\n", ans);
	return 0;
}

