#include <stdio.h>
#include <stdlib.h>
#include <sys/time.h>

#define N	100000

void srand_() {
	struct timeval tv;

	gettimeofday(&tv, NULL);
	srand(tv.tv_sec ^ tv.tv_usec);
}

int rand_(int n) {
	return (rand() * 76543LL + rand()) % n;
}

int compare(const void *a, const void *b) {
	int ia = *(int *) a;
	int ib = *(int *) b;

	return ia - ib;
}

int main() {
	static int aa[N], kk[N];
	int n, i, j, cnt, tmp;

	srand_();
	scanf("%d", &n);
	for (i = 0; i < n; i++)
		scanf("%d", &aa[i]);
	for (i = 0; i < n; i++) {
		j = rand_(i + 1);
		tmp = aa[i], aa[i] = aa[j], aa[j] = tmp;
	}
	qsort(aa, n, sizeof *aa, compare);
	cnt = 0;
	for (i = 0; i < n; i = j) {
		j = i + 1;
		while (j < n && aa[j] == aa[i])
			j++;
		if (j < n && aa[j] - aa[i] > 1) {
			printf("NO\n");
			return 0;
		}
		kk[cnt++] = j - i;
	}
	for (i = 1; i < cnt; i++)
		if ((kk[i] -= kk[i - 1]) < 0) {
			printf("NO\n");
			return 0;
		}
	printf(kk[cnt - 1] == 0 ? "YES\n" : "NO\n");
	return 0;
}

