void solve() {
	long long n,k,ans,i;
	scanf("%lld %lld", &n, &k);
	ans=0;i=1;
	n--;
	while(n>0) {
		if(n<i) {
			ans++;
			break;
		}
		if(i==k) {
			ans = ans + n/k;
			n = n%k;
		} else {
			ans++;
			n=n-i;
		}
		if(2*i<=k)
			i=i*2;
		else
			i=k;
	}
	printf("%lld\n", ans);
	return;
}

int main() {
	int t;
	scanf("%d", &t);
	while(t--) solve();
	return 0;
}

