#include <ctype.h>
#include <inttypes.h>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int
main(void)
{
	uint32_t n;
	scanf("%u", &n);
	while (n--) {
		// buffer size 17 is enough with columns/rows <= 10^6
		// RXCY:  'R', <=7 digits, 'C', <=7 digits, '\0'
		// Excel: <=7 digits base 26, <=7 digits base 10, '\0'
		char in[17], *p;
		scanf("%s\n", in);

		// test for RXCY format
		if (in[0] == 'R' && isdigit(in[1]) && (p = strchr(in, 'C'))) {
			// terminate row string, no need for conversion
			*p++ = '\0';

			// convert column to Excel format
			// this assumes ASCII compatibility
			uint32_t col = strtol(p, 0, 10);
			char excel[7], *pe = &excel[6];

			for (*pe = 0; col != 0; col /= 26)
				*--pe = 'A' + ((col - 1) % 26);
			printf("%s%s\n", pe, &in[1]);
		} else {
			// convert Excel format to number
			// this also assumes ASCII compatibility
			p = in;
			uint32_t col = 0;
			char c;
			while (isupper(c = *p++))
				col = col * 26 + c - 'A' + 1;
			printf("R%sC%"PRIu32"\n", --p, col);
		}
	}

	return 0;
}

