  while(arr[q]>i)
  q--;
  if(q>p)
    {
    t=arr[p];
    arr[p]=arr[q];
    arr[q]=t;
    }
  }
  t=arr[lower];
  arr[lower]=arr[q];
  arr[q]=t;
  return q;
}

