    for (i = 2; i < (2 << n); ++i) scanf("%d", &a[i]);
    d[0] = -1; d[1] = 0; maxd = -1;
    for (i = 2; i < (2 << n); ++i) {
        d[i] = d[i / 2] + a[i];
        if (d[i] > maxd) maxd = d[i];
    }
    for (i = 2; i < (2 << n); ++i) d[i] = maxd - d[i];

    int ans = 0;
    for (i = n - 2; i >= 0; --i) {
        for (j = 0; j < (1 << (n - i - 1)); ++j) {
            int mind = 0x70ffffff;
            for (k = (1 << n) + j * (2 << i); k < (1 << n) + (j + 1) * (2 << i); ++k)
                if (d[k] < mind) mind = d[k];
            ans += mind;
            for (k = (1 << n) + j * (2 << i); k < (1 << n) + (j + 1) * (2 << i); ++k)
                d[k] -= mind;
        }
    }
    for (i = (1 << n); i < (2 << n); ++i) ans += d[i];
    printf("%d\n", ans);

    return 0;
}

