#include <stdio.h>

#define N	200000
#define L	32
#define B	(N / L)

int min(int a, int b) { return a < b ? a : b; }

int query(int l, int r) {
	int k;

	printf("? %d %d\n", l + 1, r + 1), fflush(stdout);
	scanf("%d", &k);
	return k;
}

int main() {
	static int kk[B];
	int n, t, h;

	scanf("%d%d", &n, &t);
	for (h = 0; h < B && h * L < n; h++)
		kk[h] = L - query(h * L, min((h + 1) * L, n) - 1);
	while (t--) {
		int k, lower, upper;

		scanf("%d", &k);
		for (h = 0; h < B && h * L < n; h++) {
			if (k <= kk[h])
				break;
			k -= kk[h];
		}
		lower = h * L - 1, upper = min((h + 1) * L, n) - 1;
		while (upper - lower > 1) {
			int i = (lower + upper) / 2;

			if (k > (i - h * L + 1) - query(h * L, i))
				lower = i;
			else
				upper = i;
		}
		kk[h]--;
		printf("! %d\n", upper + 1), fflush(stdout);
	}
	return 0;
}

